// Export stats for the dashboard
export const getTotalMovies = (): number => tamilCinemaMovies.length;
export const getMoviesByGenre = (genre: string): Movie[] => tamilCinemaMovies.filter(m => m.genre === genre);
export const getMoviesByYear = (year: number): Movie[] => tamilCinemaMovies.filter(m => m.year === year);
export const getMoviesByActor = (actor: string): Movie[] => tamilCinemaMovies.filter(m => m.actor.toLowerCase().includes(actor.toLowerCase()));
export const getAverageRating = (): number => Math.round((tamilCinemaMovies.reduce((sum, m) => sum + m.rating, 0) / tamilCinemaMovies.length) * 10) / 10;
export const getTotalRevenue = (): number => tamilCinemaMovies.reduce((sum, m) => sum + m.revenue, 0);






